import { useState } from 'react';
import api from '../../api/axios';

export default function PartnerForm() {
  const [formData, setFormData] = useState({ name: '', share_percentage: '' });
  const [submitting, setSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post('/partners/', formData);
      alert('Partner saved');
      setFormData({ name: '', share_percentage: '' });
    } catch (err) {
      console.error(err);
      alert('Error: ' + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-6">
      <form onSubmit={submit} className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded">
        <h2 className="text-xl font-semibold">Add Partner</h2>

        <input name="name" placeholder="Partner Name" className="w-full border p-2 rounded" value={formData.name} onChange={handleChange} required />
        <input name="share_percentage" placeholder="Share Percentage" type="number" className="w-full border p-2 rounded" value={formData.share_percentage} onChange={handleChange} />

        <button disabled={submitting} className="w-full bg-blue-600 text-white py-2 rounded">{submitting ? 'Saving...' : 'Save'}</button>
      </form>
    </div>
  );
}
