// src/components/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { format } from 'date-fns';

const API_BASE = 'http://localhost:8000/api';

const Dashboard = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [dailyIncomes, setDailyIncomes] = useState([]);
  const [monthlyProfits, setMonthlyProfits] = useState([]);
  const [ships, setShips] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch ships
  const fetchShips = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/ships/`);
      const shipData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setShips(shipData);
    } catch (error) {
      console.error('Error fetching ships:', error);
      setShips([]);
    }
  }, []);

  // Fetch daily incomes
  const fetchDailyIncome = useCallback(async () => {
    try {
      const dateStr = format(selectedDate, 'yyyy-MM-dd');
      const response = await axios.get(`${API_BASE}/incomes/daily_summary/?date=${dateStr}`);
      const incomeData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setDailyIncomes(incomeData);
    } catch (error) {
      console.error('Error fetching daily income:', error);
      setDailyIncomes([]);
    }
  }, [selectedDate]);

  // Fetch monthly profits
  const fetchMonthlyProfits = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/profits/`);
      const profitData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setMonthlyProfits(profitData);
    } catch (error) {
      console.error('Error fetching monthly profits:', error);
      setMonthlyProfits([]);
    }
  }, []);

  useEffect(() => {
    fetchShips();
    fetchDailyIncome();
    fetchMonthlyProfits();
  }, [fetchShips, fetchDailyIncome, fetchMonthlyProfits]);

  const calculateProfit = async (shipId) => {
    setLoading(true);
    try {
      const monthStr = format(selectedDate, 'yyyy-MM');
      await axios.post(`${API_BASE}/profits/calculate_profit/`, {
        ship_id: shipId,
        month: monthStr
      });
      fetchMonthlyProfits();
      alert('Profit calculated successfully!');
    } catch (error) {
      alert('Error calculating profit: ' + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
    }
  };

  const totalDailyIncome = Array.isArray(dailyIncomes)
    ? dailyIncomes.reduce((sum, income) => sum + parseFloat(income.amount || 0), 0)
    : 0;

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Ship Management</h1>
        
        {/* Daily Income Card */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Daily Income - {format(selectedDate, 'MMMM dd, yyyy')}</h2>
            <input
              type="date"
              value={format(selectedDate, 'yyyy-MM-dd')}
              onChange={(e) => setSelectedDate(new Date(e.target.value))}
              className="border rounded px-3 py-2"
            />
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4 mb-4">
            {Array.isArray(ships) && ships.map(ship => {
              const shipIncome = Array.isArray(dailyIncomes)
                ? dailyIncomes
                    .filter(income => income.ship === ship.id)
                    .reduce((sum, income) => sum + parseFloat(income.amount || 0), 0)
                : 0;
              
              return (
                <div key={ship.id} className="bg-blue-50 rounded-lg p-4">
                  <h3 className="font-semibold text-blue-800">{ship.name}</h3>
                  <p className="text-2xl font-bold text-blue-600">
                    ₹{(shipIncome / 10000000).toFixed(2)} Cr
                  </p>
                </div>
              );
            })}
          </div>
          
          <div className="bg-purple-100 rounded-lg p-4">
            <h3 className="font-semibold text-green-800">Total Daily Income</h3>
            <p className="text-3xl font-bold text-green-600">
              ₹{(totalDailyIncome / 10000000).toFixed(2)} Cr
            </p>
          </div>
        </div>

        {/* Monthly Profits */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4">Monthly Profits</h2>
          <div className="flex space-x-4 mb-4">
            {Array.isArray(ships) && ships.map(ship => (
              <button
                key={ship.id}
                onClick={() => calculateProfit(ship.id)}
                disabled={loading}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:bg-blue-300"
              >
                Calculate {ship.name} Profit
              </button>
            ))}
          </div>
          
          <div className="overflow-x-auto">
            <table className="min-w-full table-auto">
              <thead>
                <tr className="bg-gray-50">
                  <th className="px-4 py-2">Ship</th>
                  <th className="px-4 py-2">Month</th>
                  <th className="px-4 py-2">Income</th>
                  <th className="px-4 py-2">Expenses</th>
                  <th className="px-4 py-2">Net Profit</th>
                </tr>
              </thead>
              <tbody>
                {Array.isArray(monthlyProfits) && monthlyProfits.map(profit => (
                  <tr key={profit.id} className="border-b">
                    <td className="px-4 py-2">{profit.ship_name}</td>
                    <td className="px-4 py-2">{format(new Date(profit.month), 'MMM yyyy')}</td>
                    <td className="px-4 py-2">₹{(profit.total_income / 10000000).toFixed(2)}Cr</td>
                    <td className="px-4 py-2">₹{(profit.total_expenses / 10000000).toFixed(2)}Cr</td>
                    <td className={`px-4 py-2 font-semibold ${
                      profit.net_profit >= 0 ? 'text-green-600' : 'text-red-600'
                    }`}>
                      ₹{(profit.net_profit / 10000000).toFixed(2)}Cr
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
