import { useState } from "react";
import api from "../../api/axios";

export default function DailyIncomeForm() {
  const [formData, setFormData] = useState({ ship: "", amount: "", date: "" });
  const [submitting, setSubmitting] = useState(false);
  const [canCancel, setCanCancel] = useState(false);
  const [lastCreatedId, setLastCreatedId] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const res = await api.post("/incomes/", formData);
      alert("Daily income recorded");
      setFormData({ ship: "", amount: "", date: "" });
      setCanCancel(true);
      setLastCreatedId(res.data?.id || null);
    } catch (err) {
      console.error(err);
      alert("Error: " + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center p-6"
      style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1511379938547-c1f69b13d835?w=1920&h=1080&fit=crop')`,
      }}
    >
      <div className="absolute inset-0 bg-black/30"></div>
      <form
        onSubmit={submit}
        className="relative max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Daily Income</h2>

        <input
          name="ship"
          placeholder="Ship ID"
          className="w-full border p-2 rounded"
          value={formData.ship}
          onChange={handleChange}
        />
        <input
          name="amount"
          placeholder="Amount"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.amount}
          onChange={handleChange}
        />
        <input
          name="date"
          type="date"
          className="w-full border p-2 rounded"
          value={formData.date}
          onChange={handleChange}
        />

        <div className="flex gap-3">
          <button
            type="submit"
            disabled={submitting}
            className="flex-1 bg-blue-600 text-white py-2 rounded"
          >
            {submitting ? "Saving..." : "Save"}
          </button>
          <button
            type="button"
            onClick={async () => {
              if (!lastCreatedId) { setCanCancel(false); return; }
              try {
                await api.delete(`/incomes/${lastCreatedId}/`);
                alert('Daily income deleted');
                setCanCancel(false);
                setLastCreatedId(null);
              } catch (err) {
                console.error('Cancel income failed', err);
                alert('Cancel failed: ' + (err.response?.data?.detail || err.message));
              }
            }}
            disabled={!canCancel}
            className={`px-4 py-2 rounded ${canCancel ? 'bg-red-500 text-white' : 'bg-gray-200 text-gray-500 cursor-not-allowed'}`}
          >
            âœ– Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
