import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function ExpenseForm() {
  const [formData, setFormData] = useState({
    description: "",
    amount: "",
    date: "",
    ship: "",
    category: "",
    purchase_from: "",
  });
  const [ships, setShips] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [document, setDocument] = useState(null);
  const [documentName, setDocumentName] = useState("");
  const [canCancel, setCanCancel] = useState(false);
  const [lastCreatedId, setLastCreatedId] = useState(null);

  useEffect(() => {
    const fetchShips = async () => {
      try {
        const res = await api.get("/ships/");
        const data = Array.isArray(res.data)
          ? res.data
          : res.data.results || [];
        setShips(data);
      } catch (err) {
        console.error("Error fetching ships", err);
      }
    };
    fetchShips();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setDocument(file);
      setDocumentName(file.name);
    }
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      // If a document is attached, use FormData to send file + form data
      let res;
      if (document) {
        const uploadData = new FormData();
        uploadData.append("description", formData.description);
        uploadData.append("amount", formData.amount);
        uploadData.append("date", formData.date);
        uploadData.append("ship", formData.ship);
        uploadData.append("category", formData.category);
        uploadData.append("purchase_from", formData.purchase_from);
        uploadData.append("document", document);

        res = await api.post("/expenses/", uploadData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
      } else {
        // No document, send as JSON
        res = await api.post("/expenses/", formData);
      }

      alert("Expense recorded");
      setFormData({
        description: "",
        amount: "",
        date: "",
        ship: "",
        category: "",
        purchase_from: "",
      });
      setDocument(null);
      setDocumentName("");
      setCanCancel(true);
      setLastCreatedId(res?.data?.id || null);
    } catch (err) {
      console.error(err);
      alert(
        "Error saving expense: " + (err.response?.data?.detail || err.message)
      );
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center p-6"
      style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1454165804606-c3d57bc86b40?w=1920&h=1080&fit=crop')`,
      }}
    >
      <div className="absolute inset-0 bg-black/30"></div>
      <form
        onSubmit={submit}
        className="relative max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Expense</h2>

        <input
          name="description"
          placeholder="Description"
          className="w-full border p-2 rounded"
          value={formData.description}
          onChange={handleChange}
          required
        />

        <input
          name="amount"
          placeholder="Amount"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.amount}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="date"
          className="w-full border p-2 rounded"
          value={formData.date}
          onChange={handleChange}
          required
        />

        <select
          name="ship"
          value={formData.ship}
          onChange={handleChange}
          className="w-full border p-2 rounded"
          required
        >
          <option value="">Select Ship</option>
          {ships.map((s) => (
            <option key={s.id} value={s.id}>
              {s.name}
            </option>
          ))}
        </select>

        <select
          name="category"
          value={formData.category}
          onChange={handleChange}
          className="w-full border p-2 rounded"
          required
        >
          <option value="">Select Category</option>
          <option value="FUEL">Fuel</option>
          <option value="MAINTENANCE">Maintenance</option>
          <option value="CREW">Crew Salary</option>
          <option value="REPAIR">Repairs</option>
          <option value="INSURANCE">Insurance</option>
          <option value="OTHER">Other</option>
        </select>

        <input
          name="purchase_from"
          placeholder="Purchase from (vendor/supplier name)"
          className="w-full border p-2 rounded"
          value={formData.purchase_from}
          onChange={handleChange}
        />

        <label className="block">
          <span className="text-sm text-gray-700">
            Upload Document (optional)
          </span>
          <input
            type="file"
            onChange={handleFileChange}
            className="w-full border p-2 rounded mt-1"
            accept=".pdf,.jpg,.jpeg,.png,.doc,.docx,.xlsx,.xls"
          />
          {documentName && (
            <p className="text-sm text-green-600 mt-1">ðŸ“„ {documentName}</p>
          )}
        </label>

        <div className="flex gap-3">
          <button
            type="submit"
            disabled={submitting}
            className="flex-1 bg-blue-600 text-white py-2 rounded"
          >
            {submitting ? "Saving..." : "Save"}
          </button>
          <button
            type="button"
            onClick={async () => {
              if (!lastCreatedId) {
                setCanCancel(false);
                return;
              }
              try {
                await api.delete(`/expenses/${lastCreatedId}/`);
                alert("Expense deleted");
                setCanCancel(false);
                setLastCreatedId(null);
              } catch (err) {
                console.error("Cancel expense failed", err);
                alert(
                  "Cancel failed: " +
                    (err.response?.data?.detail || err.message)
                );
              }
            }}
            disabled={!canCancel}
            className={`px-4 py-2 rounded ${
              canCancel
                ? "bg-red-500 text-white"
                : "bg-gray-200 text-gray-500 cursor-not-allowed"
            }`}
          >
            âœ– Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
