import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function PartnerForm() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    share_amount: "",
    share_percentage: "",
    ship: "",
  });
  const [ships, setShips] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [loadingShips, setLoadingShips] = useState(false);
  const [selectedShipCost, setSelectedShipCost] = useState(null);
  const [showForm, setShowForm] = useState(true);
  const [partners, setPartners] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchShips();
    fetchPartners();
  }, []);

  const fetchShips = async () => {
    setLoadingShips(true);
    try {
      const res = await api.get("/ships/");
      const data = Array.isArray(res.data)
        ? res.data
        : res.data.results || [];
      setShips(data);
    } catch (err) {
      console.error("Error fetching ships", err);
    } finally {
      setLoadingShips(false);
    }
  };

  const fetchPartners = async () => {
    setLoading(true);
    try {
      const res = await api.get("/partners/");
      const data = Array.isArray(res.data)
        ? res.data
        : res.data.results || [];
      setPartners(data);
    } catch (err) {
      console.error("Error fetching partners", err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };

    // If ship is changed, update selectedShipCost and recalculate share_percentage
    if (name === "ship") {
      const selected = ships.find((s) => s.id === parseInt(value));
      const cost = selected ? parseFloat(selected.purchase_cost) : null;
      setSelectedShipCost(cost);

      // Recalculate share_percentage if share_amount exists
      if (updatedFormData.share_amount && cost) {
        const shareAmount = parseFloat(updatedFormData.share_amount);
        const percentage = (shareAmount / cost) * 100;
        updatedFormData.share_percentage = percentage.toFixed(2);
      } else {
        updatedFormData.share_percentage = "";
      }
    }

    // If share_amount is changed, recalculate share_percentage
    if (name === "share_amount") {
      if (selectedShipCost && value) {
        const shareAmount = parseFloat(value);
        const percentage = (shareAmount / selectedShipCost) * 100;
        updatedFormData.share_percentage = percentage.toFixed(2);
      } else {
        updatedFormData.share_percentage = "";
      }
    }

    setFormData(updatedFormData);
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const res = await api.post("/partners/", formData);
      alert("Partner saved");
      setFormData({
        name: "",
        email: "",
        phone: "",
        share_amount: "",
        share_percentage: "",
        ship: "",
      });
      setSelectedShipCost(null);
    } catch (err) {
      console.error(err);
      alert("Error: " + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center p-6"
      style={{
        backgroundImage: `url(/titanic-iceberg.jpg)`,
      }}
    >
      <div className="absolute inset-0 bg-black/30"></div>
      <form
        onSubmit={submit}
        className="relative max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Partner</h2>

        <select
          name="ship"
          value={formData.ship}
          onChange={handleChange}
          className="w-full border p-2 rounded"
          disabled={loadingShips}
          required
        >
          <option value="">Select Ship</option>
          {ships.map((s) => (
            <option key={s.id} value={s.id}>
              {s.name}
            </option>
          ))}
        </select>

        <input
          name="name"
          placeholder="Partner Name"
          className="w-full border p-2 rounded"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <input
          name="email"
          type="email"
          placeholder="Email Address"
          className="w-full border p-2 rounded"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <input
          name="phone"
          placeholder="Contact Number"
          className="w-full border p-2 rounded"
          value={formData.phone}
          onChange={handleChange}
          required
        />

        <input
          name="share_amount"
          placeholder="Share Amount"
          type="number"
          step="0.01"
          className="w-full border p-2 rounded"
          value={formData.share_amount}
          onChange={handleChange}
          required
        />

        <input
          name="share_percentage"
          placeholder="Share Percentage (Auto-calculated)"
          type="number"
          step="0.01"
          className="w-full border p-2 rounded bg-gray-100"
          value={formData.share_percentage}
          readOnly
        />

        <div className="flex">
          <button
            type="submit"
            disabled={submitting}
            className="flex-1 bg-blue-600 text-white py-2 rounded"
          >
            {submitting ? "Saving..." : "Save"}
          </button>
        </div>
      </form>
    </div>
  );
}
