import { useState } from "react";
import api from "../../api/axios";

export default function DailyIncomeForm() {
  const [formData, setFormData] = useState({ ship: "", amount: "", date: "" });
  const [submitting, setSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post("/incomes/", formData);
      alert("Daily income recorded");
      setFormData({ ship: "", amount: "", date: "" });
    } catch (err) {
      console.error(err);
      alert("Error: " + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-6">
      <form
        onSubmit={submit}
        className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded"
      >
        <h2 className="text-xl font-semibold">Add Daily Income</h2>

        <input
          name="ship"
          placeholder="Ship ID"
          className="w-full border p-2 rounded"
          value={formData.ship}
          onChange={handleChange}
        />
        <input
          name="amount"
          placeholder="Amount"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.amount}
          onChange={handleChange}
        />
        <input
          name="date"
          type="date"
          className="w-full border p-2 rounded"
          value={formData.date}
          onChange={handleChange}
        />

        <button
          disabled={submitting}
          className="w-full bg-blue-600 text-white py-2 rounded"
        >
          {submitting ? "Saving..." : "Save"}
        </button>
      </form>
    </div>
  );
}
