import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function ShipForm() {
  const [formData, setFormData] = useState({
    name: "",
    purchase_cost: "",
    purchase_date: "",
    is_active: true,
  });

  const [submitting, setSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post("/ships/", formData);
      alert("Ship created!");
      setFormData({
        name: "",
        purchase_cost: "",
        purchase_date: "",
        is_active: true,
      });
    } catch (err) {
      console.error(err);
      alert(
        "Error creating ship: " + (err.response?.data?.detail || err.message)
      );
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-sky-400 to-purple-500 p-6">
      <form
        onSubmit={submit}
        className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Ship</h2>

        <input
          name="name"
          placeholder="Ship Name"
          className="w-full border p-2 rounded"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <input
          name="purchase_cost"
          placeholder="Purchase Cost (Tk)"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.purchase_cost}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="purchase_date"
          className="w-full border p-2 rounded"
          value={formData.purchase_date}
          onChange={handleChange}
          required
        />

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            name="is_active"
            checked={formData.is_active}
            onChange={handleChange}
          />
          Active
        </label>

        <button
          disabled={submitting}
          className="w-full bg-blue-600 text-white py-2 rounded"
        >
          {submitting ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}
