import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();

  const links = [
    { to: "/", label: "Dashboard" },
    { to: "/reports", label: "Reports" },
    { to: "/ships", label: "Ship Entry" },
    { to: "/expenses", label: "Expense Entry" },
    { to: "/daily-income", label: "Daily Income Entry" },
    { to: "/projects", label: "Project Entry" },
  ];

  const [openSettings, setOpenSettings] = useState(false);

  return (
    <aside className="w-80 bg-white border-r min-h-screen">
      <div className="p-4 border-b">
        <h2 className="text-lg font-bold">Ship Management</h2>
      </div>
      <nav className="p-4 space-y-2">
        {links.map((l) => (
          <Link
            key={l.to}
            to={l.to}
            className={`block px-3 py-2 rounded hover:bg-gray-100 ${
              location.pathname === l.to ? "bg-gray-100 font-semibold" : ""
            }`}
          >
            {l.label}
          </Link>
        ))}

        <div>
          <button
            type="button"
            onClick={() => setOpenSettings((s) => !s)}
            className="w-full text-left px-3 py-2 rounded hover:bg-gray-100"
          >
            Settings
          </button>

          {openSettings && (
            <div className="pl-4 mt-2 space-y-1">
              <Link to="/partners" className={`block px-2 py-1 rounded hover:bg-gray-100 ${location.pathname === '/partners' ? 'bg-gray-100 font-semibold' : ''}`}>
                Partner Entry (with Share)
              </Link>
            </div>
          )}
        </div>
      </nav>
    </aside>
  );
};

export default Sidebar;
