// src/components/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { format, subMonths } from 'date-fns';

const API_BASE = 'http://localhost:8000/api';

const Dashboard = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [dailyIncomes, setDailyIncomes] = useState([]);
  const [monthlyProfits, setMonthlyProfits] = useState([]);
  const [ships, setShips] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch ships
  const fetchShips = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/ships/`);
      const shipData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setShips(shipData);
    } catch (error) {
      console.error('Error fetching ships:', error);
    }
  }, []);

  // Fetch all daily incomes
  const fetchDailyIncomes = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/incomes/`);
      const incomeData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setDailyIncomes(incomeData);
    } catch (error) {
      console.error('Error fetching daily incomes:', error);
    }
  }, []);

  // Fetch all expenses
  const fetchExpenses = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/expenses/`);
      const expenseData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setExpenses(expenseData);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  }, []);

  // Fetch monthly profits
  const fetchMonthlyProfits = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE}/profits/`);
      const profitData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setMonthlyProfits(profitData);
    } catch (error) {
      console.error('Error fetching monthly profits:', error);
    }
  }, []);

  useEffect(() => {
    fetchShips();
    fetchDailyIncomes();
    fetchExpenses();
    fetchMonthlyProfits();
  }, [fetchShips, fetchDailyIncomes, fetchExpenses, fetchMonthlyProfits]);

  const calculateProfit = async () => {
    setLoading(true);
    try {
      const monthStr = format(selectedDate, 'yyyy-MM');
      // Calculate profit for all ships
      const promises = ships.map(ship =>
        axios.post(`${API_BASE}/profits/calculate_profit/`, {
          ship_id: ship.id,
          month: monthStr
        })
      );
      await Promise.all(promises);
      fetchMonthlyProfits();
      alert('Profit calculated successfully for all ships!');
    } catch (error) {
      alert('Error calculating profit: ' + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
    }
  };

  // Calculate daily income for selected date
  const todayIncomes = dailyIncomes.filter(
    inc => inc.date === format(selectedDate, 'yyyy-MM-dd')
  );
  const todayTotal = todayIncomes.reduce((sum, inc) => sum + parseFloat(inc.amount || 0), 0);

  // Calculate monthly income and expenses for selected month
  const monthStr = format(selectedDate, 'yyyy-MM');
  const monthIncomes = dailyIncomes.filter(inc => inc.date?.startsWith(monthStr));
  const monthlyTotal = monthIncomes.reduce((sum, inc) => sum + parseFloat(inc.amount || 0), 0);
  
  const monthExpenses = expenses.filter(exp => exp.date?.startsWith(monthStr));
  const monthlyExpenseTotal = monthExpenses.reduce((sum, exp) => sum + parseFloat(exp.amount || 0), 0);

  // Calculate trend (today vs average daily for month)
  const avgDailyForMonth = monthIncomes.length > 0 ? monthlyTotal / monthIncomes.length : 0;
  const trend = todayTotal - avgDailyForMonth;
  const trendPercent = avgDailyForMonth > 0 ? ((trend / avgDailyForMonth) * 100).toFixed(1) : 0;

  // Ship-wise daily income for selected date
  const shipWiseIncome = ships.map(ship => ({
    ship,
    dayIncome: todayIncomes
      .filter(inc => inc.ship === ship.id)
      .reduce((sum, inc) => sum + parseFloat(inc.amount || 0), 0),
    thisMonthIncome: monthIncomes
      .filter(inc => inc.ship === ship.id)
      .reduce((sum, inc) => sum + parseFloat(inc.amount || 0), 0),
    lastMonthIncome: dailyIncomes
      .filter(inc => inc.ship === ship.id && inc.date?.startsWith(format(subMonths(selectedDate, 1), 'yyyy-MM')))
      .reduce((sum, inc) => sum + parseFloat(inc.amount || 0), 0)
  }));

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-8xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Ship Management Dashboard</h1>

        {/* Date Selector */}
        <div className="mb-6">
          <input
            type="date"
            value={format(selectedDate, 'yyyy-MM-dd')}
            onChange={(e) => setSelectedDate(new Date(e.target.value))}
            className="border rounded px-4 py-2 text-lg"
          />
        </div>

        {/* Daily Income Summary */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          {/* Today's Income Card */}
          <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg shadow-lg p-6 text-white">
            <h2 className="text-lg font-semibold mb-2">Today's Income</h2>
            <p className="text-4xl font-bold mb-4">Tk {todayTotal.toFixed(2)}</p>
            <div className="flex items-center gap-2">
              <span className={`flex items-center gap-1 px-3 py-1 rounded-full font-semibold ${
                trend >= 0 ? 'bg-green-400 text-green-900' : 'bg-red-400 text-red-900'
              }`}>
                {trend >= 0 ? '↑' : '↓'} {Math.abs(trendPercent)}%
              </span>
              <span className="text-sm opacity-90">vs monthly avg</span>
            </div>
          </div>

          {/* Monthly Income Card */}
          <div className="bg-gradient-to-br from-green-500 to-green-600 rounded-lg shadow-lg p-6 text-white">
            <h2 className="text-lg font-semibold mb-2">Monthly Income</h2>
            <p className="text-4xl font-bold mb-4">Tk {monthlyTotal.toFixed(2)}</p>
            <div className="text-sm opacity-90">
              {monthIncomes.length} days recorded
            </div>
          </div>
        </div>

        {/* Ship-wise Daily Income */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4">Ship-wise Income</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {shipWiseIncome.map(({ ship, dayIncome, thisMonthIncome, lastMonthIncome }) => {
              const trend = thisMonthIncome - lastMonthIncome;
              const trendPercent = lastMonthIncome > 0 ? ((trend / lastMonthIncome) * 100).toFixed(1) : 0;
              
              return (
                <div key={ship.id} className="border rounded-lg p-4 hover:shadow-md transition">
                  <h3 className="font-semibold text-gray-800 mb-3 text-lg">{ship.name}</h3>
                  
                  {/* Day Income */}
                  <div className="mb-3 pb-3 border-b">
                    <p className="text-xs text-gray-500">Day Income</p>
                    <p className="text-xl font-bold text-blue-600">Tk {dayIncome.toFixed(2)}</p>
                  </div>

                  {/* This Month Income */}
                  <div className="mb-3 pb-3 border-b">
                    <p className="text-xs text-gray-500">This Month Income</p>
                    <p className="text-xl font-bold text-green-600">Tk {thisMonthIncome.toFixed(2)}</p>
                  </div>

                  {/* Last Month Income + Trend */}
                  <div>
                    <p className="text-xs text-gray-500">Last Month Income</p>
                    <div className="flex items-center justify-between">
                      <p className="text-xl font-bold text-gray-700">Tk {lastMonthIncome.toFixed(2)}</p>
                      <span className={`flex items-center gap-1 px-2 py-1 rounded text-xs font-semibold ${
                        trend >= 0 ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                      }`}>
                        {trend >= 0 ? '↑' : '↓'} {Math.abs(trendPercent)}%
                      </span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Income vs Expenses Comparison */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4">Monthly Summary - {format(selectedDate, 'MMMM yyyy')}</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Income */}
            <div className="border-l-4 border-green-500 pl-4">
              <p className="text-gray-600 text-sm">Total Income</p>
              <p className="text-3xl font-bold text-green-600">Tk {monthlyTotal.toFixed(2)}</p>
            </div>

            {/* Expenses */}
            <div className="border-l-4 border-red-500 pl-4">
              <p className="text-gray-600 text-sm">Total Expenses</p>
              <p className="text-3xl font-bold text-red-600">Tk {monthlyExpenseTotal.toFixed(2)}</p>
            </div>

            {/* Net Profit */}
            <div className={`border-l-4 ${monthlyTotal - monthlyExpenseTotal >= 0 ? 'border-green-500' : 'border-red-500'} pl-4`}>
              <p className="text-gray-600 text-sm">Net Profit</p>
              <p className={`text-3xl font-bold ${monthlyTotal - monthlyExpenseTotal >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                Tk {(monthlyTotal - monthlyExpenseTotal).toFixed(2)}
              </p>
            </div>
          </div>
        </div>

        {/* Monthly Profits Table */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Monthly Profits History</h2>
            <button
              onClick={() => calculateProfit()}
              disabled={loading}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:bg-blue-300"
            >
              {loading ? 'Calculating...' : `Calculate Profit - ${format(selectedDate, 'MMM yyyy')}`}
            </button>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full">
              <thead>
                <tr className="bg-gray-50 border-b">
                  <th className="px-4 py-3 text-left font-semibold">Ship</th>
                  <th className="px-4 py-3 text-left font-semibold">Month</th>
                  <th className="px-4 py-3 text-right font-semibold">Income</th>
                  <th className="px-4 py-3 text-right font-semibold">Expenses</th>
                  <th className="px-4 py-3 text-right font-semibold">Net Profit</th>
                  <th className="px-4 py-3 text-center font-semibold">Trend</th>
                </tr>
              </thead>
              <tbody>
                {Array.isArray(monthlyProfits) && monthlyProfits.length > 0 ? (
                  monthlyProfits.map((profit, idx) => {
                    const prevProfit = idx > 0 ? monthlyProfits[idx - 1]?.net_profit : profit.net_profit;
                    const profitTrend = profit.net_profit - prevProfit;
                    return (
                      <tr key={profit.id} className="border-b hover:bg-gray-50">
                        <td className="px-4 py-3">{profit.ship_name}</td>
                        <td className="px-4 py-3">{format(new Date(profit.month), 'MMM yyyy')}</td>
                        <td className="px-4 py-3 text-right">Tk {parseFloat(profit.total_income || 0).toFixed(2)}</td>
                        <td className="px-4 py-3 text-right">Tk {parseFloat(profit.total_expenses || 0).toFixed(2)}</td>
                        <td className={`px-4 py-3 text-right font-semibold ${
                          profit.net_profit >= 0 ? 'text-green-600' : 'text-red-600'
                        }`}>
                          Tk {parseFloat(profit.net_profit || 0).toFixed(2)}
                        </td>
                        <td className="px-4 py-3 text-center">
                          <span className={`inline-flex items-center gap-1 px-2 py-1 rounded-full text-sm font-semibold ${
                            profitTrend >= 0 ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                          }`}>
                            {profitTrend >= 0 ? '↑' : '↓'} {Math.abs(profitTrend).toFixed(0)}
                          </span>
                        </td>
                      </tr>
                    );
                  })
                ) : (
                  <tr>
                    <td colSpan="6" className="px-4 py-3 text-center text-gray-500">
                      No profit data available. Click "Calculate" to generate profit reports.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
