import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function ShipForm() {
  const [formData, setFormData] = useState({
    name: "",
    purchase_cost: "",
    purchase_date: "",
    is_active: true,
  });

  const [submitting, setSubmitting] = useState(false);
  const [canCancel, setCanCancel] = useState(false);
  const [lastCreatedId, setLastCreatedId] = useState(null);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const res = await api.post("/ships/", formData);
      alert("Ship created!");
      setFormData({
        name: "",
        purchase_cost: "",
        purchase_date: "",
        is_active: true,
      });
      setCanCancel(true);
      setLastCreatedId(res.data?.id || null);
    } catch (err) {
      console.error(err);
      alert(
        "Error creating ship: " + (err.response?.data?.detail || err.message)
      );
    } finally {
      setSubmitting(false);
    }
  };

  const cancel = async () => {
    if (!lastCreatedId) {
      setCanCancel(false);
      return;
    }
    try {
      await api.delete(`/ships/${lastCreatedId}/`);
      alert("Ship creation canceled and record deleted");
      setCanCancel(false);
      setLastCreatedId(null);
    } catch (err) {
      console.error("Cancel ship failed", err);
      alert("Cancel failed: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center p-6"
      style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1505142468610-359e7d316be0?w=1920&h=1080&fit=crop')`,
      }}
    >
      <div className="absolute inset-0 bg-black/30"></div>
      <form
        onSubmit={submit}
        className="relative max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Ship</h2>

        <input
          name="name"
          placeholder="Ship Name"
          className="w-full border p-2 rounded"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <input
          name="purchase_cost"
          placeholder="Purchase Cost (Tk)"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.purchase_cost}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="purchase_date"
          className="w-full border p-2 rounded"
          value={formData.purchase_date}
          onChange={handleChange}
          required
        />

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            name="is_active"
            checked={formData.is_active}
            onChange={handleChange}
          />
          Active
        </label>

        <div className="flex gap-3">
          <button
            type="submit"
            disabled={submitting}
            className="flex-1 bg-blue-600 text-white py-2 rounded"
          >
            {submitting ? "Submitting..." : "Submit"}
          </button>

          <button
            type="button"
            onClick={cancel}
            disabled={!canCancel}
            className={`px-4 py-2 rounded ${
              canCancel
                ? "bg-red-500 text-white"
                : "bg-gray-200 text-gray-500 cursor-not-allowed"
            }`}
            title={
              canCancel
                ? "Cancel created ship"
                : "Cancel (available after save)"
            }
          >
            âœ– Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
