import React from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();

  const links = [
    { to: "/", label: "Dashboard" },
    { to: "/reports", label: "Reports" },
    { to: "/ships", label: "Ship Entry" },
    { to: "/expenses", label: "Expense Entry" },
  ];

  return (
    <aside className="w- bg-white border-r min-h-screen">
      <div className="p-4 border-b">
        <h2 className="text-lg font-bold">Ship Management</h2>
      </div>
      <nav className="p-4 space-y-2">
        {links.map((l) => (
          <Link
            key={l.to}
            to={l.to}
            className={`block px-3 py-2 rounded hover:bg-gray-100 ${
              location.pathname === l.to ? "bg-gray-100 font-semibold" : ""
            }`}
          >
            {l.label}
          </Link>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
