import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function ShipForm() {
  const [formData, setFormData] = useState({
    name: "",
    purchase_cost: "",
    purchase_date: "",
    is_active: true,
  });

  const [submitting, setSubmitting] = useState(false);
  const [showForm, setShowForm] = useState(true);
  const [ships, setShips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchShips();
  }, []);

  const fetchShips = async () => {
    setLoading(true);
    try {
      const res = await api.get("/ships/");
      const data = Array.isArray(res.data) ? res.data : res.data.results || [];
      setShips(data);
    } catch (err) {
      console.error("Error fetching ships", err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const resetForm = () => {
    setFormData({
      name: "",
      purchase_cost: "",
      purchase_date: "",
      is_active: true,
    });
    setEditingId(null);
  };

  const handleCancel = () => {
    resetForm();
    setShowForm(false);
  };

  const handleEdit = (ship) => {
    setFormData(ship);
    setEditingId(ship.id);
    setShowForm(true);
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      // Ensure purchase_cost is sent as a number (no commas) and validate integer digits
      let cost =
        formData.purchase_cost === ""
          ? null
          : parseFloat(String(formData.purchase_cost));

      if (cost !== null) {
        if (isNaN(cost) || cost <= 0) {
          alert("Purchase cost must be a positive number.");
          setSubmitting(false);
          return;
        }
        const integerDigits = Math.floor(Math.abs(cost)).toString().length;
        if (integerDigits > 8) {
          alert(
            "Purchase cost has too many digits before the decimal. Maximum 8 digits allowed (e.g. 99999999.99)."
          );
          setSubmitting(false);
          return;
        }
        // Round to two decimals to match backend DecimalField
        cost = Math.round(cost * 100) / 100;
      }

      const payload = { ...formData, purchase_cost: cost };

      const res = await api.post("/ships/", payload);
      alert("Ship created!");
      setFormData({
        name: "",
        purchase_cost: "",
        purchase_date: "",
        is_active: true,
      });
    } catch (err) {
      console.error("Ship create error:", err.response || err);
      const serverData = err.response?.data;
      const message = serverData
        ? JSON.stringify(serverData)
        : err.response?.data?.detail || err.message;
      alert("Error creating ship: " + message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center p-6"
      style={{
        backgroundImage: `url(/titanic-iceberg.jpg)`,
      }}
    >
      <div className="absolute inset-0 bg-black/30"></div>
      <form
        onSubmit={submit}
        className="relative max-w-lg mx-auto space-y-4 p-6 bg-white shadow-lg rounded"
      >
        <h2 className="text-xl font-semibold">Add Ship</h2>

        <input
          name="name"
          placeholder="Ship Name"
          className="w-full border p-2 rounded"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <input
          name="purchase_cost"
          placeholder="Purchase Cost (Tk) â€” max 8 digits before decimal"
          type="number"
          step="0.01"
          min="0"
          max="99999999.99"
          className="w-full border p-2 rounded"
          value={formData.purchase_cost}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="purchase_date"
          className="w-full border p-2 rounded"
          value={formData.purchase_date}
          onChange={handleChange}
          required
        />

        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            name="is_active"
            checked={formData.is_active}
            onChange={handleChange}
          />
          Active
        </label>

        <button
          disabled={submitting}
          className="w-full bg-blue-600 text-white py-2 rounded"
        >
          {submitting ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}
