// src/components/PartnerReport.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { format } from 'date-fns';

const API_BASE = 'http://localhost:8000/api';

const PartnerReport = () => {
  const [selectedMonth, setSelectedMonth] = useState(format(new Date(), 'yyyy-MM'));
  const [partners, setPartners] = useState([]);
  const [ships, setShips] = useState([]);
  const [monthlyProfits, setMonthlyProfits] = useState([]);

  // Fetch ships, partners, and monthly profits whenever selectedMonth changes
  useEffect(() => {
    fetchShips();
    fetchPartners();
    fetchMonthlyProfits();
  }, [selectedMonth]);

  const fetchShips = async () => {
    try {
      const response = await axios.get(`${API_BASE}/ships/`);
      const shipData = Array.isArray(response.data)
        ? response.data
        : response.data.results || [];
      setShips(shipData);
    } catch (error) {
      console.error('Error fetching ships:', error);
      setShips([]);
    }
  };

  const fetchPartners = async () => {
    try {
      const res = await axios.get(`${API_BASE}/partners/`);
      const data = res.data;
      const partnerData = Array.isArray(data) ? data : data.results || [];
      setPartners(partnerData);
    } catch (error) {
      console.error('Error fetching partners:', error);
      setPartners([]);
    }
  };

  const fetchMonthlyProfits = async () => {
    try {
      const res = await axios.get(`${API_BASE}/profits/`);
      const data = res.data;
      const profitData = Array.isArray(data) ? data : data.results || [];
      setMonthlyProfits(profitData);
    } catch (error) {
      console.error('Error fetching monthly profits:', error);
      setMonthlyProfits([]);
    }
  };

  // Filter partners and profits by selected month
  const filteredProfits = Array.isArray(monthlyProfits)
    ? monthlyProfits.filter(profit => profit.month?.startsWith(selectedMonth))
    : [];

  const printReport = () => {
    window.print();
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-8xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6 print:shadow-none">
          {/* Header */}
          <div className="flex justify-between items-center mb-6 print:mb-4">
            <h1 className="text-2xl font-bold">Partner Share Report</h1>
            <div className="flex space-x-4 no-print">
              <input
                type="month"
                value={selectedMonth}
                onChange={e => setSelectedMonth(e.target.value)}
                className="border rounded px-3 py-2"
              />
              <button
                onClick={printReport}
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
              >
                Print Report
              </button>
            </div>
          </div>

          {/* Report Content */}
          <div className="space-y-6">
            {Array.isArray(ships) && ships.map(ship => {
              const shipPayouts = filteredPayouts.filter(p => p.ship_name === ship.name);
              const totalShipPayout = shipPayouts.reduce((sum, p) => sum + parseFloat(p.share_amount || 0), 0);

              if (!shipPayouts.length) return null;

              return (
                <div key={ship.id} className="border rounded-lg p-4 page-break">
                  <h2 className="text-xl font-semibold mb-4 border-b pb-2">
                    {ship.name} - {format(new Date(selectedMonth + '-01'), 'MMMM yyyy')}
                  </h2>

                  <table className="min-w-full mb-4">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="px-4 py-2 text-left">Partner Name</th>
                        <th className="px-4 py-2 text-left">Share %</th>
                        <th className="px-4 py-2 text-right">Share Amount</th>
                        <th className="px-4 py-2 text-center">Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {shipPayouts.map(payout => (
                        <tr key={payout.id} className="border-b">
                          <td className="px-4 py-2">{payout.partner_name}</td>
                          <td className="px-4 py-2">{payout.partner?.share_percentage || 0}%</td>
                          <td className="px-4 py-2 text-right">
                            Tk {((payout.share_amount || 0) / 10000000).toFixed(2)} Cr
                          </td>
                          <td className="px-4 py-2 text-center">
                            <span className={`px-2 py-1 rounded ${
                              payout.paid ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                            }`}>
                              {payout.paid ? 'Paid' : 'Pending'}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                    <tfoot>
                      <tr className="bg-gray-50 font-semibold">
                        <td colSpan="2" className="px-4 py-2">Total</td>
                        <td className="px-4 py-2 text-right">
                          Tk {(totalShipPayout / 10000000).toFixed(2)} Cr
                        </td>
                        <td className="px-4 py-2"></td>
                      </tr>
                    </tfoot>
                  </table>

                  <div className="mt-6 text-sm text-gray-600">
                    <p>Generated on: {format(new Date(), 'MMMM dd, yyyy')}</p>
                    <p>Ship Cost: Tk 3.00 Crores</p>
                    <p>Number of Partners: {shipPayouts.length}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PartnerReport;
