import { useState } from 'react';
import api from '../../api/axios';

export default function ProjectForm() {
  const [formData, setFormData] = useState({ name: '', start_date: '', end_date: '', budget: '' });
  const [submitting, setSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post('/projects/', formData);
      alert('Project created');
      setFormData({ name: '', start_date: '', end_date: '', budget: '' });
    } catch (err) {
      console.error(err);
      alert('Error: ' + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-6">
      <form onSubmit={submit} className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded">
        <h2 className="text-xl font-semibold">Add Project</h2>

        <input name="name" placeholder="Project Name" className="w-full border p-2 rounded" value={formData.name} onChange={handleChange} required />
        <input name="start_date" type="date" className="w-full border p-2 rounded" value={formData.start_date} onChange={handleChange} />
        <input name="end_date" type="date" className="w-full border p-2 rounded" value={formData.end_date} onChange={handleChange} />
        <input name="budget" placeholder="Budget" type="number" className="w-full border p-2 rounded" value={formData.budget} onChange={handleChange} />

        <button disabled={submitting} className="w-full bg-blue-600 text-white py-2 rounded">{submitting ? 'Saving...' : 'Save'}</button>
      </form>
    </div>
  );
}
