import { useState } from "react";
import API from "../../api";

export default function ShipForm() {
  const [formData, setFormData] = useState({
    name: "",
    purchase_cost: "",
    purchase_date: "",
    is_active: true,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({ ...formData, [name]: type === "checkbox" ? checked : value });
  };

  const submit = async (e) => {
    e.preventDefault();
    await API.post("/ships/", formData);
    alert("Ship created!");
  };

  return (
    <form onSubmit={submit} className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded">

      <h2 className="text-xl font-semibold">Add Ship</h2>

      <input
        name="name"
        placeholder="Ship Name"
        className="w-full border p-2 rounded"
        value={formData.name}
        onChange={handleChange}
      />

      <input
        name="purchase_cost"
        placeholder="Purchase Cost"
        type="number"
        className="w-full border p-2 rounded"
        value={formData.purchase_cost}
        onChange={handleChange}
      />

      <input
        type="date"
        name="purchase_date"
        className="w-full border p-2 rounded"
        value={formData.purchase_date}
        onChange={handleChange}
      />

      <label className="flex items-center gap-2">
        <input type="checkbox" name="is_active" checked={formData.is_active} onChange={handleChange} />
        Active
      </label>

      <button className="w-full bg-blue-600 text-white py-2 rounded">Submit</button>
    </form>
  );
}
