import { useState, useEffect } from "react";
import api from "../../api/axios";

export default function PartnerForm() {
  const [formData, setFormData] = useState({
    name: "",
    share_percentage: "",
    ship: "",
  });
  const [ships, setShips] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [loadingShips, setLoadingShips] = useState(false);

  useEffect(() => {
    const fetchShips = async () => {
      setLoadingShips(true);
      try {
        const res = await api.get("/ships/");
        const data = Array.isArray(res.data)
          ? res.data
          : res.data.results || [];
        setShips(data);
      } catch (err) {
        console.error("Error fetching ships", err);
      } finally {
        setLoadingShips(false);
      }
    };
    fetchShips();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post("/partners/", formData);
      alert("Partner saved");
      setFormData({ name: "", share_percentage: "", ship: "" });
    } catch (err) {
      console.error(err);
      alert("Error: " + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-6">
      <form
        onSubmit={submit}
        className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded"
      >
        <h2 className="text-xl font-semibold">Add Partner</h2>

        <input
          name="name"
          placeholder="Partner Name"
          className="w-full border p-2 rounded"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          name="name"
          placeholder="Contact Number"
          className="w-full border p-2 rounded"
          value={formData.contact}
          onChange={handleChange}
          required
        />
        <input
          name="share_percentage"
          placeholder="Share Percentage"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.share_percentage}
          onChange={handleChange}
          required
        />

        <label className="block">
          <span className="text-sm text-gray-700">
            Attach to Ship (optional)
          </span>
          <select
            name="ship"
            value={formData.ship}
            onChange={handleChange}
            className="w-full border p-2 rounded mt-1"
            disabled={loadingShips}
          >
            <option value="">-- Select Ship --</option>
            {ships.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name}
              </option>
            ))}
          </select>
        </label>

        <button
          disabled={submitting}
          className="w-full bg-blue-600 text-white py-2 rounded"
        >
          {submitting ? "Saving..." : "Save"}
        </button>
      </form>
    </div>
  );
}
