import { useState, useEffect } from 'react';
import api from '../../api/axios';

export default function ExpenseForm() {
  const [formData, setFormData] = useState({
    description: '',
    amount: '',
    date: '',
    ship: '',
  });
  const [ships, setShips] = useState([]);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchShips = async () => {
      try {
        const res = await api.get('/ships/');
        const data = Array.isArray(res.data) ? res.data : res.data.results || [];
        setShips(data);
      } catch (err) {
        console.error('Error fetching ships', err);
      }
    };
    fetchShips();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      await api.post('/expenses/', formData);
      alert('Expense recorded');
      setFormData({ description: '', amount: '', date: '', ship: '' });
    } catch (err) {
      console.error(err);
      alert('Error saving expense: ' + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-6">
      <form onSubmit={submit} className="max-w-lg mx-auto space-y-4 p-6 bg-white shadow rounded">
        <h2 className="text-xl font-semibold">Add Expense</h2>

        <input
          name="description"
          placeholder="Description"
          className="w-full border p-2 rounded"
          value={formData.description}
          onChange={handleChange}
          required
        />

        <input
          name="amount"
          placeholder="Amount"
          type="number"
          className="w-full border p-2 rounded"
          value={formData.amount}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="date"
          className="w-full border p-2 rounded"
          value={formData.date}
          onChange={handleChange}
          required
        />

        <select name="ship" value={formData.ship} onChange={handleChange} className="w-full border p-2 rounded">
          <option value="">Select Ship (optional)</option>
          {ships.map(s => (
            <option key={s.id} value={s.id}>{s.name}</option>
          ))}
        </select>

        <button disabled={submitting} className="w-full bg-blue-600 text-white py-2 rounded">{submitting ? 'Saving...' : 'Save'}</button>
      </form>
    </div>
  );
}
